"use strict";$(function(){var a={data:{num:1,price:5},init:function(){a.clickElement()},clickElement:function(){$(".list li").click(a.listClick),$(".add").click(a.add),$(".reduce").click(a.reduce)},listClick:function(){$(this).siblings("li").removeClass("active"),$(this).addClass("active"),$(".pay").html($(this).find("span").html())},add:function(){a.data.num++,$(".num input").val(a.data.num),$(".pay").html(a.data.price*a.data.num)},reduce:function(){a.data.num<=1?a.data.num=1:a.data.num--,$(".num input").val(a.data.num),$(".pay").html(a.data.price*a.data.num)}};a.init()});